import React, { useState, useRef, useEffect } from 'react';
import { Calculator, Save, Share, Download, AlertCircle } from 'lucide-react';
import Card from '../components/common/Card';
import Button from '../components/common/Button';
import Input from '../components/common/Input';
import { estimatesAPI } from '../utils/api';
import toast from 'react-hot-toast';

const EstimatePage = () => {
  const [formData, setFormData] = useState({
    projectName: '',
    constructionType: 'new_construction',
    buildingType: '',
    location: '',
    dataPeriod: '3months',
    projectDescription: '',
  });
  const [counties, setCounties] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [charCount, setCharCount] = useState(0);
  const [validationErrors, setValidationErrors] = useState({});

  const onFileSelected = (e) => {
    const f = e.target.files && e.target.files[0];
    if (f) setSelectedFile(f);
  };

  const onDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    const f = e.dataTransfer.files && e.dataTransfer.files[0];
    if (f) setSelectedFile(f);
  };

  const onDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };

  const uploadFile = async () => {
    if (!selectedFile) return toast.error('Please select a file to upload');
    const id = toast.loading('Uploading estimate...');
    try {
      const res = await estimatesAPI.uploadEstimate(selectedFile);
      toast.dismiss(id);
      toast.success('Estimate uploaded successfully');
      console.log('Upload response', res.data);
      if (res.data.row_errors && res.data.row_errors.length > 0) {
        setValidationErrors(res.data.row_errors);
      } else {
        setValidationErrors([]);
      }
    } catch (err) {
      toast.dismiss(id);
      console.error(err);
      const message = err?.response?.data?.error || 'Upload failed';
      toast.error(message);
    }
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Cost Estimation</h1>
        <p className="mt-2 text-gray-600">Create detailed construction cost estimates for your projects.</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2">
          <Card>
            <Card.Header>
              <div className="flex items-center justify-between">
                <h2 className="text-lg font-semibold text-gray-900">Project Details</h2>
                <div className="space-x-2">
                  <button onClick={() => setMode('manual')} className={`px-3 py-1 rounded ${mode === 'manual' ? 'bg-indigo-600 text-white' : 'bg-gray-100 text-gray-700'}`}>Manual</button>
                  <button onClick={() => setMode('upload')} className={`px-3 py-1 rounded ${mode === 'upload' ? 'bg-indigo-600 text-white' : 'bg-gray-100 text-gray-700'}`}>Upload Plan</button>
                </div>
              </div>
            </Card.Header>

            <Card.Body>
              {mode === 'upload' ? (
                <div className="space-y-4">
                  <label className="block text-sm font-medium text-gray-700">Upload Excel Plan</label>
                  <div
                    className="border-2 border-dashed border-gray-200 rounded p-6 text-center hover:border-indigo-400 transition cursor-pointer"
                    onClick={() => fileInputRef.current && fileInputRef.current.click()}
                    onDrop={onDrop}
                    onDragOver={onDragOver}
                  >
                    <UploadCloud className="mx-auto w-10 h-10 text-gray-400" />
                    <p className="mt-2 text-gray-600">Drag & drop or click to upload an Excel (.xlsx) plan</p>
                    <p className="mt-1 text-sm text-gray-400">Use sheets named 'Estimate' and 'Items' for best results</p>
                    {selectedFile && (
                      <div className="mt-4 text-left">
                        <FileText className="inline w-4 h-4 mr-2 text-gray-600" />
                        <span className="font-medium">{selectedFile.name}</span>
                      </div>
                    )}
                  </div>
                  <input ref={fileInputRef} type="file" accept=".xlsx,.xls" className="hidden" onChange={onFileSelected} />
                  <div className="flex space-x-3">
                    <Button onClick={uploadFile}>Upload & Create Estimate</Button>
                    <Button variant="secondary" onClick={() => { setSelectedFile(null); if (fileInputRef.current) fileInputRef.current.value = null; }}>Cancel</Button>
                  </div>
                  {validationErrors && validationErrors.length > 0 && (
                    <div className="mt-4 bg-yellow-50 border border-yellow-200 p-3 rounded">
                      <div className="font-medium text-yellow-800">Validation issues in uploaded items</div>
                      <ul className="mt-2 list-disc list-inside text-sm text-yellow-800">
                        {validationErrors.map((err, i) => (
                          <li key={i}>Row {err.row}: {err.error}</li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              ) : (
                <div className="space-y-6">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Project Name</label>
                      <input type="text" className="input" placeholder="Enter project name" />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Project Type</label>
                      <select className="input">
                        <option value="">Select project type</option>
                        <option value="2bedroom">2-Bedroom House</option>
                        <option value="3bedroom">3-Bedroom House</option>
                        <option value="commercial">Commercial Building</option>
                        <option value="wall">Perimeter Wall</option>
                      </select>
                    </div>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Location</label>
                      <input type="text" className="input" placeholder="Enter location" />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Total Area (sqm)</label>
                      <input type="number" className="input" placeholder="Enter total area" />
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Project Description</label>
                    <textarea className="input" rows={4} placeholder="Describe your project..." />
                  </div>
                </div>
              )}
            </Card.Body>
          </Card>
        </div>

        <div className="lg:col-span-1">
          <Card>
            <Card.Header>
              <h2 className="text-lg font-semibold text-gray-900">Cost Summary</h2>
            </Card.Header>
            <Card.Body>
              <div className="space-y-4">
                <div className="flex justify-between">
                  <span className="text-gray-600">Base Cost</span>
                  <span className="font-medium">Ksh0.00</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Materials</span>
                  <span className="font-medium">Ksh00.00</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Labor</span>
                  <span className="font-medium">Ksh0.00</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Equipment</span>
                  <span className="font-medium">Ksh0.00</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Contingency (10%)</span>
                  <span className="font-medium">Ksh0.00</span>
                </div>
                <hr />
                <div className="flex justify-between text-lg font-bold">
                  <span>Total Cost</span>
                  <span>Ksh0.00</span>
                </div>
              </div>

              <div className="mt-6 space-y-3">
                <Button className="w-full">
                  <Calculator className="w-4 h-4 mr-2" />
                  Calculate Estimate
                </Button>
                <Button variant="secondary" className="w-full">
                  <Save className="w-4 h-4 mr-2" />
                  Save Draft
                </Button>
                <Button variant="secondary" className="w-full">
                  <Share className="w-4 h-4 mr-2" />
                  Share
                </Button>
                <Button variant="secondary" className="w-full">
                  <Download className="w-4 h-4 mr-2" />
                  Export Report
                </Button>
              </div>
            </Card.Body>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default EstimatePage;
